import { FormsModule } from '@angular/forms';

@Component({
  selector: 'app-student-dashboard',
  imports:[CommonModule,FormsModule],
  templateUrl: './student-dashboard.component.html',
  styleUrls: ['./student-dashboard.component.css']
})
export class StudentDashboardComponent implements OnInit {
  student = {
    name: 'John Doe',
    email: 'johndoe@example.com',
    totalCourses: 5,
    pendingAssignments: 2,
    completedModules: 12
  };

  // üîπ Active Section
  activeSection: string | null = null;

  // üîπ Quick Access Cards
  cards = [
    { key: 'courses', name: 'üìñ My Courses' },
    { key: 'assignments', name: 'üìù Assignments' },
    { key: 'attendance', name: 'üìä Attendance' },
    { key: 'discussions', name: 'üí¨ Discussions' }
  ];

  // üîπ Enrolled Courses (Added `category`)
  enrolledCourses = [
    {
      name: 'HTML Basics: Building Your First Web Page',
      description: 'Learn the basics of web development with HTML and CSS.',
      image: 'data:image/jpeg;base64,',
      category: 'Web Development'
    },
    {
      name: 'Modern JavaScript from Beginner to Advanced',
      description: 'Master JavaScript fundamentals and start building interactive websites.',
      image: 'data:image/jpeg;base64,',
      category: 'Programming'
    },
    {
      name: 'Angular for Beginners',
      description: 'A comprehensive guide to Angular framework and its features.',
      image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcShMF4-ocDUoG9c_Z3Te0zsVaYMJUyIqyc80Q&s',
      category: 'Web Development'
    }
  ];

  markTask(day: number) {
    const dateKey = `${this.selectedYear}-${this.selectedMonth}-${day}`;
    
    if (this.markedDates.includes(day)) {
      this.markedDates = this.markedDates.filter(d => d !== day);
      localStorage.removeItem(dateKey);
    } else {
      this.markedDates.push(day);
      localStorage.setItem(dateKey, JSON.stringify({ day, month: this.selectedMonth, year: this.selectedYear }));
    }
  }
  recommendedCourses: { name: string; category: string; image: string; description: string }[] = [];
  // üîπ Recommended Courses
  // recommendedCourses = [
  //   { name: 'Node.js Mastery', category: 'Backend', image: 'node.jpg', description: 'Deep dive into Node.js' },
  //   { name: 'Python for AI', category: 'AI/ML', image: 'python.jpg', description: 'AI concepts with Python' }
  // ];

  // üîπ Assignments Data
  assignments = [
    { course: 'Mathematics', startDate: '10-02-2025', dueDate: '20-02-2025', status: 'Pending' },
    { course: 'C++ Programming', startDate: '05-02-2025', dueDate: '18-02-2025', status: 'Submitted' },
    { course: 'Web Development', startDate: '12-02-2025', dueDate: '22-02-2025', status: 'Pending' }
  ];

  // üîπ Line Chart Data
  lineChartPoints = '10,150 30,120 50,180 70,100 90,160 110,140 130,180 150,120';

  // üîπ Calendar Data
  selectedYear: number = new Date().getFullYear();
  selectedMonth: number = new Date().getMonth() + 1;
  weekDays = ['Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun'];
  daysInMonth: number[] = [];
  emptyCells: number[] = [];
  markedDates: number[] = [];

  // üîπ Years & Months for Selection
  years: number[] = [];
  months = [
    'January', 'February', 'March', 'April', 'May', 'June',
    'July', 'August', 'September', 'October', 'November', 'December'
  ];

  ngOnInit() {
    this.populateYears();
    this.generateCalendar(this.selectedMonth, this.selectedYear);
    this.loadMarkedDates();
    this.generateRecommendations(); 
  }

  // üîπ Toggle Sections
  showSection(section: string) {
    this.activeSection = this.activeSection === section ? null : section;

    // Generate recommendations when 'My Courses' is opened
    if (section === 'courses') {
      this.generateRecommendations();
    }
  }
  loadMarkedDates() {
    this.markedDates = [];
  
    for (let i = 1; i <= 31; i++) {
      const dateKey = `${this.selectedYear}-${this.selectedMonth}-${i}`;
      if (localStorage.getItem(dateKey)) {
        this.markedDates.push(i);
      }
    }
  }
  isCurrentDay(day: number): boolean {
    const today = new Date();
    return day === today.getDate() && this.selectedMonth === today.getMonth() + 1 && this.selectedYear === today.getFullYear();
  }
  

  // üîπ Generate AI-Based Recommendations Based on Enrolled Courses
  generateRecommendations() {
    const allCourses = [
      { 
        name: 'Mastering React Hooks & Context API', 
        category: 'Web Development',
        image: 'react.jpg', 
        description: 'Learn how to manage state efficiently in React applications using Hooks and Context API.' 
      },
      { 
        name: 'JavaScript Mastery', 
        category: 'Web Development',
        image: 'js.jpg', 
        description: 'Master JavaScript with advanced concepts, ES6+, and hands-on projects.' 
      },
      { 
        name: 'Advanced Web Design with HTML & CSS', 
        category: 'Design',
        image: 'uiux.jpg', 
        description: 'Learn modern UI/UX design principles with responsive HTML & CSS techniques.' 
      }
      
     
    ];

    // Filter out courses with categories already enrolled
    const enrolledCategories = this.enrolledCourses
    .map(course => course.category || 'Unknown') // ‚úÖ Avoids errors for missing category
    .filter(category => category !== 'Unknown');

    this.recommendedCourses = allCourses.filter(course =>
      !enrolledCategories.includes(course.category)
    );
  }

  // üîπ Generate Calendar for Selected Year & Month
  generateCalendar(month: number, year: number) {
    this.daysInMonth = [];
    this.emptyCells = [];

    const firstDay = new Date(year, month - 1, 1).getDay();
    const totalDays = new Date(year, month, 0).getDate();

    // Convert Sunday (0) to Monday-first format (6)
    const adjustedFirstDay = firstDay === 0 ? 6 : firstDay - 1;

    // Create empty cells before first day
    this.emptyCells = new Array(adjustedFirstDay).fill(0);

    // Generate days
    for (let i = 1; i <= totalDays; i++) {
      this.daysInMonth.push(i);
    }
  }

  // üîπ Populate Year Selection (Last 5 & Next 5 Years)
  populateYears() {
    const currentYear = new Date().getFullYear();
    this.years = Array.from({ length: 11 }, (_, i) => currentYear - 5 + i);
  }

  // üîπ Update Calendar When Year or Month Changes
  updateCalendar() {
    this.generateCalendar(this.selectedMonth, this.selectedYear);
  }

  // üîπ Mark Important Dates
  
}
